#import "BiDaListRequest.h"
#import "GlobalKeyMacro.h"
#import "HttpRequestFactory.h"
#import "HttpSessionClient.h"
#import "HttpRequestFactory.h"

@implementation BiDaListRequest

- (NSString *)relativeURLPath {
    return [NSString stringWithFormat:@"%@link/getDuang.do?userStr=", [HttpRequestFactory prefixUrl]];
}

- (NSDictionary*)allParams {
    if (_pageSize && _time && _handStyle && _type) {
        NSMutableDictionary *dic = [NSMutableDictionary dictionary];
        [dic setValue:_pageSize forKey:@"pageSize"];
        [dic setValue:_time forKey:@"time"];
        [dic setValue:_handStyle forKey:@"handStyle"];
        [dic setValue:_type forKey:@"type"];
        return dic;
    } else {
        return nil;
    }
}

- (void)sendWithSuccessBlock:(SuccessBlock)successBlock failureBlock:(FailureBlock)failureBlock {
    self.params = [self allParams];
    if (self.params) {
    	self.successBlock = successBlock;
        self.failureBlock = failureBlock;
        self.requestMethod = REQUEST_METHOD_POST;
        [[HttpSessionClient instance] requestWithRequest:self];
    }
}

@end